import styles from "./LocationItem.module.css";
import Link from "next/link";
import { useState } from "react";

export default ({ type, id }) => {
  const [src, setSrc] = useState(`/locations/${type}/${type}.png`);

  return (
    <Link href="/location/[id]" as={`/location/${id}`}>
      <a className={styles.link}>
        <div className={styles.card}>
          <img
            onError={() => setSrc("/locations/unknown/unknown.png")}
            src={src}
          />
          <div className={styles.info}>
            <h3 className={styles.title}>Earth (C-137)</h3>
            <p className={styles.description}>{type}</p>
          </div>
        </div>
      </a>
    </Link>
  );
};
