import gql from "graphql-tag";
import { useQuery } from "@apollo/react-hooks";
import withApollo from "../hooks/withApollo";

import LocationItem from "../components/LocationItem/LocationItem";
import Page from "../components/Page/Page";

const QUERY = () => gql`
  {
    locations {
      results {
        type
        id
        name
        residents {
          image
        }
      }
    }
  }
`;

const Index = () => {
  const { loading, data, error } = useQuery(QUERY());

  const renderPage = () => {
    return (
      <img src="https://cdn.dribbble.com/users/1897588/screenshots/3887258/gif_export.gif" />
    );
    // if (loading) {
    //   return (
    //     <img src="https://cdn.dribbble.com/users/1897588/screenshots/3887258/gif_export.gif" />
    //   );
    // }
    // if (error) return <h1>error</h1>;
    // if (data) {
    //   console.log(data);
    //   const {
    //     locations: { results }
    //   } = data;
    //   return results.map(props => <LocationItem key={props.id} {...props} />);
    // }
  };

  return renderPage();
};

export default withApollo(Index);
