import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { useQuery, useApolloClient } from "@apollo/react-hooks";
import InfiniteScroll from "react-infinite-scroller";
import gql from "graphql-tag";
import Page from "../../components/Page/Page";
import Layout from "../../components/Layout/Layout";
import Loader from "../../components/Loader/Loader";
import LocationInfo from "../../components/LocationInfo/LocationInfo";
import ResidentsItem from "../../components/ResidentsItem/ResidentsItem";

const QUERY = query => gql`
 {
  currentResidentsPage @client
  location(id: ${query}) {
    name
    type
    residents {
      image
      name
      type
      id
    }
  }
}
`;

const LocationPage = () => {
  const { query } = useRouter();
  const { loading, data, error } = useQuery(QUERY(query.id));
  const client = useApolloClient()

  const renderPage = () => {
    if (loading) return <Loader />;
    if (error) return <h1>error</h1>;
    if (data) {
      const {
        location: { name, type, residents },
        currentResidentsPage
      } = data;
      const start = 20 * currentResidentsPage;
      const end = 20 * (currentResidentsPage + 1);
      const pageData = residents.splice(
        20 * currentResidentsPage,
        20 * (currentResidentsPage + 1)
      );

      // console.log(start, end, currentResidentsPage, pageData);
      console.log(data);
      return (
        <>
          <LocationInfo type={type} name={name} />
          <InfiniteScroll
            initialLoad={false}
            loader={<Loader />}
            pageStart={0}
            loadMore={() => {
              client.writeData({
                data: { currentResidentsPage: currentResidentsPage++ }
              });
            }}
            hasMore={(residents.length / 20 - currentResidentsPage) > 0}
          >
            <Layout title="Residents">
              {pageData.map(resident => (
                <ResidentsItem
                  key={resident.id}
                  location={name}
                  {...resident}
                />
              ))}
            </Layout>
          </InfiniteScroll>
        </>
      );
    }
  };

  return <Page href="/">{renderPage()}</Page>;
};

export default LocationPage;
