          <h3>Residents</h3>
          <Layout>
            {residents.map(({ image }) => (
              <img src={image} />
            ))}
          </Layout>
        </>
      );
    }
  };

  return <Page breadcrumb="/">{renderPage()}</Page>;
};

export default LocationPage;
